    /*
     * Listing of contasapagar
     */
    function index()
    {
        $params['limit'] = RECORDS_PER_PAGE;
        $params['offset'] = ($this->input->get('per_page')) ? $this->input->get('per_page') : 0;

        $config = $this->config->item('pagination');
        $config['base_url'] = site_url('contaapagar/index?');
        $config['total_rows'] = $this->Contasapagar_model->get_all_contasapagar_count();
        $this->pagination->initialize($config);

        $data['contasapagar'] = $this->Contasapagar_model->get_all_contasapagar($params);

        $data['_view'] = 'contasapagar/index';
        $this->load->view('layouts/main',$data);
    }

    /*
     * Adding a new contaapagar
     */
    function add()
    {
        $this->load->library('form_validation');

		$this->form_validation->set_rules('datavencimento','Datavencimento','required');
		$this->form_validation->set_rules('valorconta','Valorconta','required');

		if($this->form_validation->run())
        {
            $params = array(
				'datavencimento' => $this->input->post('datavencimento'),
				'datapagamento' => $this->input->post('datapagamento'),
				'valorconta' => $this->input->post('valorconta'),
				'desconto' => $this->input->post('desconto'),
				'valorpago' => $this->input->post('valorpago'),

				'status' => $this->input->post('status'),
				'datalanc' => $this->input->post('datalanc'),
				'descricao' => $this->input->post('descricao'),
				'numerodoc' => $this->input->post('numerodoc'),
				'tipomovimentacao' => $this->input->post('tipomovimentacao'),
            );

            $contaapagar_id = $this->Contasapagar_model->add_contaapagar($params);
            redirect('contasapagar/index');
        }
        else
        {
            $data['_view'] = 'contasapagar/add';
            $this->load->view('layouts/main',$data);
        }
    }

