    function edit($idcontasapagar)
    {
        // check if the contaapagar exists before trying to edit it
        $data['contaapagar'] = $this->Contasapagar_model->get_contaapagar($idcontasapagar);

        if(isset($data['contaapagar']['idcontasapagar']))
        {
            $this->load->library('form_validation');

			$this->form_validation->set_rules('datavencimento','Datavencimento','required');
			$this->form_validation->set_rules('valorconta','Valorconta','required');

			if($this->form_validation->run())
            {
                $params = array(
					'datavencimento' => $this->input->post('datavencimento'),
					'datapagamento' => $this->input->post('datapagamento'),
					'valorconta' => $this->input->post('valorconta'),
					'desconto' => $this->input->post('desconto'),
					'valorpago' => $this->input->post('valorpago'),

					'status' => $this->input->post('status'),
					'datalanc' => $this->input->post('datalanc'),
					'descricao' => $this->input->post('descricao'),
					'numerodoc' => $this->input->post('numerodoc'),
					'tipomovimentacao' => $this->input->post('tipomovimentacao'),
                );

                $this->Contasapagar_model->update_contaapagar($idcontasapagar,$params);
                redirect('contasapagar/index');
            }
            else
            {
                $data['_view'] = 'contasapagar/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The contaapagar you are trying to edit does not exist.');
    }

    /*
     * Deleting contaapagar
     */
    function remove($idcontasapagar)
    {
        $contaapagar = $this->Contasapagar_model->get_contaapagar($idcontasapagar);

        // check if the contaapagar exists before trying to delete it
        if(isset($contaapagar['idcontasapagar']))
        {
            $this->Contasapagar_model->delete_contaapagar($idcontasapagar);
            redirect('contasapagar/index');
        }
        else
            show_error('The contaapagar you are trying to delete does not exist.');
    }

}
